schema:
{
  "collections": [
    {
      "name": "vehicle_detections",
      "document": {
        "properties": {
          "detection_id": {
            "bsonType": "string",
            "description": "Unique identifier for the detection event"
          },
          "camera_id": { "bsonType": "string" },
          "timestamp": { "bsonType": "date" },
          "vehicle_type": {
            "bsonType": "string",
            "enum": ["car", "truck", "motorcycle", "bus", "van"]
          },
          "license_plate": { "bsonType": "string" },
          "color": { "bsonType": "string" },
          "speed": { "bsonType": "double", "description": "In km/h" },
          "direction": { "bsonType": "string", "enum": ["north", "south", "east", "west"] },
          "confidence_score": { "bsonType": "double", "description": "Detection confidence between 0 and 1" },
          "bounding_box": {
            "bsonType": "object",
            "properties": {
              "x": { "bsonType": "int" },
              "y": { "bsonType": "int" },
              "width": { "bsonType": "int" },
              "height": { "bsonType": "int" }
            }
          },
          "make_model": { "bsonType": "string" },
          "attributes": {
            "bsonType": "object",
            "properties": {
              "has_sunroof": { "bsonType": "bool" },
              "num_passengers": { "bsonType": "int" }
            }
          }
        }
      }
    }
  ],
  "version": 1
}

{
  "collections": [
    {
      "name": "person_detections",
      "document": {
        "properties": {
          "detection_id": {
            "bsonType": "string",
            "description": "Unique identifier for the detection event"
          },
          "camera_id": { "bsonType": "string" },
          "timestamp": { "bsonType": "date" },
          "bounding_box": {
            "bsonType": "object",
            "properties": {
              "x": { "bsonType": "int" },
              "y": { "bsonType": "int" },
              "width": { "bsonType": "int" },
              "height": { "bsonType": "int" }
            }
          },
          "confidence_score": { "bsonType": "double", "description": "Detection confidence between 0 and 1" },
          "gender": { "bsonType": "string", "enum": ["male", "female", "unknown"] },
          "age_group": { "bsonType": "string", "enum": ["child", "teen", "adult", "senior"] },
          "clothing": {
            "bsonType": "object",
            "properties": {
              "top_color": { "bsonType": "string" },
              "bottom_color": { "bsonType": "string" }
            }
          },
          "direction": { "bsonType": "string", "enum": ["north", "south", "east", "west"] },
          "speed": { "bsonType": "double", "description": "In m/s" },
          "facial_attributes": {
            "bsonType": "object",
            "properties": {
              "has_glasses": { "bsonType": "bool" },
              "has_beard": { "bsonType": "bool" }
            }
          }
        }
      }
    }
  ],
  "version": 1
}

{
  "collections": [
    {
      "name": "crowd_density_analysis",
      "document": {
        "properties": {
          "analysis_id": { "bsonType": "string" },
          "camera_id": { "bsonType": "string" },
          "timestamp": { "bsonType": "date" },
          "zone_id": { "bsonType": "string" },
          "people_count": { "bsonType": "int" },
          "density": { "bsonType": "double", "description": "People per square meter" },
          "hotspots": {
            "bsonType": "array",
            "items": {
              "bsonType": "object",
              "properties": {
                "x": { "bsonType": "int" },
                "y": { "bsonType": "int" },
                "radius": { "bsonType": "int" },
                "density": { "bsonType": "double" }
              }
            }
          },
          "flow_vectors": {
            "bsonType": "array",
            "items": {
              "bsonType": "object",
              "properties": {
                "start_x": { "bsonType": "int" },
                "start_y": { "bsonType": "int" },
                "end_x": { "bsonType": "int" },
                "end_y": { "bsonType": "int" },
                "magnitude": { "bsonType": "double" }
              }
            }
          },
          "average_speed": { "bsonType": "double", "description": "Average speed in m/s" },
          "dwell_time": { "bsonType": "double", "description": "Average dwell time in seconds" },
          "social_distancing_compliance": { "bsonType": "double", "minimum": 0, "maximum": 1 }
        }
      }
    }
  ],
  "version": 1
}
